function x = MetroHast(T, a, b, sigq)  x = zeros([T,1]);  x(1) = 0.5;    nbBins = 20;    accept = 0;    for k = 1:T-1        xcand = Loi_de_proposition(x(k), sigq);        alpha = min(1,cibleBeta(xcand, a, b)/cibleBeta(x(k), a, b));        choice = rand();        if choice < alpha      x(k+1) = xcand;      accept = accept + 1;    else       x(k+1) = x(k);    end      end  histo = hist(x,nbBins+1);  histo = histo/sum(histo);      crossCorr = xcorr(x, "unbiased");    figure();  subplot(211)  plot(x)  title(["Display the successive x value with acceptation rate = ", num2str(accept/T * 100), '%'])    subplot(224)    plot(-T+1:T-1, crossCorr)    subplot(223)  bar(0:1/nbBins:1, histo);  
end
