function p = getProbaFromProp(x1, x2, sigq, propLoi, argsTarget)  %%%%%%%%%  %  % Permet de donner la probabilité q(x1|x2) = q(x2 - x1) en fonction de la loi  % de proposition q selectionnée à partir de propLoi : 0 -> marche aléatoire   % gaussienne, 1 -> marche aléatoire de Langevin.   %  %%%%%%%%%      if nargin < 4    argsTarget = [0.5, 0.5];  end    if propLoi == 0    %Marche aléatoire gaussienne    p = gaussianFunc((x2-x1)/sigq, 0,1);    elseif propLoi == 1   %Marche aléatoire de Langevin    eps = 0.01;    p = gaussianFunc((x2-x1)/sigq - 0.5*(log(cibleBeta(x2+eps, argsTarget(1), argsTarget(2))) - log(cibleBeta(x2, argsTarget(1), argsTarget(2))))/eps, 0,1);     end  
end
