close all;clear variables;pkg load signal;%Nombre d'échantillons dans la série à créerT = 10000;%Valeur propres à la loi BETA (loi cible)a = 0.5;b = 0.5;%Valeur du pas à utilisersigq = 0.5;%Loi de proposition à choisir (0 : loi normale, 1 : loi de Langevin, non fonctionnelle)loiProp = 0;%Loi cible à utiliser, deux fonctions actuellement : @cibleBeta, @gaussianTargetloiTarg = @cibleBeta;%Debut de l'algorithmex = MetroHast(T, sigq, loiProp, loiTarg,a,b);